import { initializeApp } from 'firebase/app';
import { getMessaging, getToken, onMessage } from 'firebase/messaging';

const firebaseConfig = {
  apiKey: "AIzaSyAvBQisC-kHjYC31q1yobSMJozW9ZkSDA8",
    authDomain: "text-notes-application.firebaseapp.com",
    projectId: "text-notes-application",
    storageBucket: "text-notes-application.firebasestorage.app",
    messagingSenderId: "670916339162",
    appId: "1:670916339162:web:a9d7deb211185b1222cce3",
    measurementId: "G-HHTKHDD4MX"
};

const app = initializeApp(firebaseConfig);
const messaging = getMessaging(app);

export const requestFirebaseNotificationPermission = () =>
  getToken(messaging, {
    vapidKey: 'YOUR_PUBLIC_VAPID_KEY_FROM_FIREBASE',
  });

export const onMessageListener = () =>
  new Promise((resolve) => {
    onMessage(messaging, (payload) => {
      resolve(payload);
    });
  });
