import React, { useState, useRef } from 'react';
import './Note.css';

const COLORS = ['#fff475', '#f28b82', '#ccff90', '#a7ffeb', '#aecbfa', '#d7aefb'];

const Note = ({ note, onDelete, onDragEnd }) => {
  const [content, setContent] = useState(note.content);
  const [showColors, setShowColors] = useState(false);
  const [color, setColor] = useState(note.color);
  const noteRef = useRef(null);
  const [dragging, setDragging] = useState(false);

  const updateNote = async (updateData) => {
    try {
      await fetch(`http://localhost:5000/api/notes/${note._id}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(updateData),
      });
    } catch (err) {
      console.error("Update failed", err);
    }
  };

  const handleContentBlur = () => {
    if (content !== note.content) {
      updateNote({ content });
    }
  };

  const handleColorChange = (newColor) => {
    setColor(newColor);
    setShowColors(false);
    updateNote({ color: newColor });
  };

  const togglePin = async () => {
    const updatedPinned = !note.pinned;
    await updateNote({ pinned: updatedPinned });
    window.location.reload(); // reload for now
  };

  const startDrag = (e) => {
    setDragging(true);
    const offsetX = e.clientX - noteRef.current.getBoundingClientRect().left;
    const offsetY = e.clientY - noteRef.current.getBoundingClientRect().top;

    const moveAt = (pageX, pageY) => {
      noteRef.current.style.left = pageX - offsetX + 'px';
      noteRef.current.style.top = pageY - offsetY + 'px';
    };

    moveAt(e.pageX, e.pageY);

    const onMouseMove = (e) => moveAt(e.pageX, e.pageY);

    document.addEventListener('mousemove', onMouseMove);

    document.onmouseup = async function () {
      document.removeEventListener('mousemove', onMouseMove);
      document.onmouseup = null;
      setDragging(false);

      const rect = noteRef.current.getBoundingClientRect();
      onDragEnd(note._id, rect.left, rect.top);
    };
  };

  return (
    <div
      ref={noteRef}
      className={`note ${dragging ? 'dragging' : ''}`}
      style={{ left: note.x, top: note.y, backgroundColor: color }}
      onMouseDown={startDrag}
    >
      <textarea
        className="note-content"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        onBlur={handleContentBlur}
      />
      <div className="note-toolbar">
        <button onClick={(e) => { e.stopPropagation(); setShowColors(!showColors); }}>üé®</button>
        <button onClick={(e) => { e.stopPropagation(); togglePin(); }}>
          {note.pinned ? 'üìç' : 'üìå'}
        </button>
        <button onClick={(e) => { e.stopPropagation(); onDelete(note._id); }}>üóëÔ∏è</button>
      </div>

      {showColors && (
        <div className="color-palette" onClick={(e) => e.stopPropagation()}>
          {COLORS.map((c) => (
            <div
              key={c}
              className="color-dot"
              style={{ backgroundColor: c }}
              onClick={() => handleColorChange(c)}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default Note;
