import React, { useState, useRef } from 'react';
import './Note.css';

const Note = ({ note, onDelete, onPin, onUpdate, onSectionMove }) => {
  const [showLabelInput, setShowLabelInput] = useState(false);
  const [newLabel, setNewLabel] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState(note.content || '');
  const [showColorPicker, setShowColorPicker] = useState(false);
  const noteRef = useRef(null);

  const colors = ['#FFD700', '#FFB6C1', '#98FB98', '#87CEEB', '#DDA0DD', '#F0E68C'];

  const handleAddLabel = () => {
    const trimmed = newLabel.trim();
    if (!trimmed) return;
    const updatedLabels = note.labels ? [...note.labels, trimmed] : [trimmed];
    const uniqueLabels = [...new Set(updatedLabels)];
    onUpdate({ ...note, labels: uniqueLabels });
    setNewLabel('');
    setShowLabelInput(false);
  };

  const handleRemoveLabel = (labelToRemove) => {
    const updatedLabels = note.labels.filter((l) => l !== labelToRemove);
    onUpdate({ ...note, labels: updatedLabels });
  };

  const handleContentUpdate = () => {
    setIsEditing(false);
    if (editedContent !== note.content) {
      onUpdate({ ...note, content: editedContent });
    }
  };

  const handleColorChange = (color) => {
    onUpdate({ ...note, color });
    setShowColorPicker(false);
  };

  const handleDragStart = (e) => {
    e.dataTransfer.setData('text/plain', JSON.stringify({
      noteId: note._id,
      type: 'note'
    }));
  };

  const handleDragEnd = (e) => {
    const canvas = document.querySelector('.note-canvas');
    if (!canvas) return;

    const canvasRect = canvas.getBoundingClientRect();
    const noteEl = noteRef.current;
    const newX = e.clientX - canvasRect.left - noteEl.offsetWidth / 2;
    const newY = e.clientY - canvasRect.top - noteEl.offsetHeight / 2;

    // Grid snapping (20px grid)
    const gridSize = 20;
    const snappedX = Math.round(newX / gridSize) * gridSize;
    const snappedY = Math.round(newY / gridSize) * gridSize;

    const maxX = canvas.offsetWidth - noteEl.offsetWidth;
    const maxY = canvas.offsetHeight - noteEl.offsetHeight;
    const clampedX = Math.max(0, Math.min(snappedX, maxX));
    const clampedY = Math.max(0, Math.min(snappedY, maxY));

    onUpdate({ ...note, x: clampedX, y: clampedY });
  };

  return (
    <div
      className={`note ${note.pinned ? 'pinned' : ''}`}
      style={{
        backgroundColor: note.color || '#FFD700',
        fontFamily: note.font || 'Arial',
        left: note.x,
        top: note.y,
        position: 'absolute',
        zIndex: isEditing ? 1000 : 100
      }}
      draggable
      onDragStart={handleDragStart}
      onDragEnd={handleDragEnd}
      ref={noteRef}
    >
      <div className="note-content">
        {isEditing ? (
          <textarea
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
            onBlur={handleContentUpdate}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && e.ctrlKey) {
                handleContentUpdate();
              }
            }}
            autoFocus
            placeholder="Write your note..."
            style={{ 
              fontFamily: note.font || 'Arial',
              resize: 'none',
              minHeight: '60px'
            }}
          />
        ) : (
          <p onClick={() => setIsEditing(true)}>
            {note.content?.trim() ? note.content : (
              <span className="placeholder">Click to edit...</span>
            )}
          </p>
        )}
      </div>

      {note.labels?.length > 0 && (
        <div className="note-labels">
          {note.labels.map((label, idx) => (
            <span className="note-label" key={idx}>
              {label}
              <button 
                className="remove-label-btn"
                onClick={() => handleRemoveLabel(label)}
                title="Remove label"
              >
                √ó
              </button>
            </span>
          ))}
        </div>
      )}

      {showLabelInput && (
        <div className="note-label-input">
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Add label"
            onKeyDown={(e) => {
              if (e.key === 'Enter') handleAddLabel();
              if (e.key === 'Escape') setShowLabelInput(false);
            }}
            autoFocus
          />
          <button onClick={handleAddLabel}>Add</button>
        </div>
      )}

      {showColorPicker && (
        <div className="color-picker">
          {colors.map(color => (
            <div
              key={color}
              className="color-option"
              style={{ backgroundColor: color }}
              onClick={() => handleColorChange(color)}
            />
          ))}
        </div>
      )}

      <div className="note-actions">
        <button onClick={() => onPin(note._id)} title={note.pinned ? "Unpin" : "Pin"}>
          {note.pinned ? 'üìå' : 'üìç'}
        </button>
        <button onClick={() => setShowColorPicker(prev => !prev)} title="Color">
          üé®
        </button>
        <button onClick={() => setShowLabelInput(prev => !prev)} title="Label">
          üè∑Ô∏è
        </button>
        <button onClick={() => onDelete(note._id)} title="Delete">
          üóëÔ∏è
        </button>
      </div>
    </div>
  );
};

export default Note;
