import React, { useState, useEffect } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import NoteCanvas from './components/NoteCanvas';
import LabelManager from './components/LabelManager';
import Topbar from './components/Topbar';
import axios from 'axios';

function App() {
  const [activeLabel, setActiveLabel] = useState('Notes');
  const [showLabelsPopup, setShowLabelsPopup] = useState(false);
  const [sidebarVisible, setSidebarVisible] = useState(true);
  const [notesKey, setNotesKey] = useState(0);
  const [labels, setLabels] = useState([]);
  const [notes, setNotes] = useState([]); // âœ… Added

  const toggleSidebar = () => {
    setSidebarVisible(prev => !prev);
  };

  const addNewNote = async () => {
  const newNote = {
      content: NULL, // ðŸŸ¢ Start empty
      x: Math.floor(Math.random() * 600),
      y: Math.floor(Math.random() * 400),
      color: "#ffff88",
      font: "sans-serif",
      pinned: false,
      labels: []
    };

    try {
      await axios.post('http://localhost:5000/api/notes', newNote);
      setNotesKey(prev => prev + 1);
    } catch (err) {
      console.error("Failed to add note", err);
    }
  };

  const fetchLabels = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/labels');
      const sorted = res.data.sort((a, b) => a.name.localeCompare(b.name));
      setLabels(sorted);
    } catch (err) {
      console.error('Error fetching labels:', err);
    }
  };

  const fetchNotes = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  useEffect(() => {
    fetchLabels();
    fetchNotes(); // âœ… Added
  }, []);

  return (
    <div className="app-container">
      <Topbar onToggleSidebar={toggleSidebar} />

      <div className="main-content">
        {sidebarVisible && (
          <Sidebar
            activeLabel={activeLabel}
            setActiveLabel={setActiveLabel}
            toggleLabels={setShowLabelsPopup}
            labels={labels}
          />
        )}

        <div className="note-canvas">
          <NoteCanvas
            key={notesKey}
            activeLabel={activeLabel}
            onAddNote={addNewNote}
            labels={labels}
            setLabels={setLabels}
            notes={notes} // âœ… Added if needed
          />
        </div>
      </div>

      {showLabelsPopup && activeLabel === 'Labels' && (
        <LabelManager
          setActiveLabel={setActiveLabel}
          labels={labels}
          setLabels={setLabels}
          refreshNotes={fetchNotes} // âœ… Added
        />
      )}

      {!showLabelsPopup && (
        <button className="add-note-btn" onClick={addNewNote}>ï¼‹</button>
      )}
    </div>
  );
}

export default App;
