import React, { useEffect, useState } from 'react';
import './App.css';
import Sidebar from './components/Sidebar';
import NoteCanvas from './components/NoteCanvas';
import LabelManager from './components/LabelManager';
import Topbar from './components/Topbar';
import axios from 'axios';

function App() {
  const [activeLabel, setActiveLabel] = useState('Notes');
  const [showLabelsPopup, setShowLabelsPopup] = useState(false);
  const [sidebarVisible, setSidebarVisible] = useState(true);
  const [notesKey, setNotesKey] = useState(0);
  const [labels, setLabels] = useState([]); // üî• synced global label list

  // Fetch all labels (from Label model)
  const fetchLabels = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/labels');
      setLabels(res.data.map(l => l.name));
    } catch (err) {
      console.error('Error fetching labels', err);
    }
  };

  useEffect(() => {
    fetchLabels();
  }, []);

  const toggleSidebar = () => {
    setSidebarVisible(prev => !prev);
  };

  const addNewNote = async () => {
    const newNote = {
      content: "New Note...",
      x: Math.floor(Math.random() * 600),
      y: Math.floor(Math.random() * 400),
      color: "#ffff88",
      font: "sans-serif",
      pinned: false,
      labels: []
    };

    try {
      await axios.post('http://localhost:5000/api/notes', newNote);
      setNotesKey(prev => prev + 1);
      fetchLabels(); // üîÅ re-fetch labels in case new labels were added
    } catch (err) {
      console.error("Failed to add note", err);
    }
  };

  return (
    <div className="app-container">
      <Topbar onToggleSidebar={toggleSidebar} />

      <div className="main-content">
        {sidebarVisible && (
          <Sidebar
            activeLabel={activeLabel}
            setActiveLabel={setActiveLabel}
            toggleLabels={setShowLabelsPopup}
            labels={labels} // ‚úÖ send label list to sidebar
          />
        )}

        <div className="note-canvas">
          <NoteCanvas
            key={notesKey}
            activeLabel={activeLabel}
            onAddNote={addNewNote}
            fetchLabels={fetchLabels} // ‚úÖ allow canvas to sync labels too
          />
        </div>
      </div>

      {showLabelsPopup && activeLabel === 'Labels' && (
        <LabelManager
          activeLabel={activeLabel}
          setActiveLabel={setActiveLabel}
          labels={labels}
          setLabels={setLabels} // ‚úÖ allow updating from LabelManager
        />
      )}

      {!showLabelsPopup && (
        <button className="add-note-btn" onClick={addNewNote}>Ôºã</button>
      )}
    </div>
  );
}

export default App;
