import React from 'react';
import axios from 'axios';
import Note from './Note';
import './NoteCanvas.css';

const NoteCanvas = ({
  activeLabel,
  labels,
  setLabels,
  notes,
  setNotes,
  fetchNotes,
  moveToTrash
}) => {
  const updateNote = async (updatedNote) => {
    const isBlank = !updatedNote.content?.trim();

    if (updatedNote.tempId && isBlank) {
      setNotes(prev => prev.filter(n => n.tempId !== updatedNote.tempId));
      return;
    }

    if (updatedNote._id && isBlank) {
      try {
        await axios.delete(`http://localhost:5000/api/notes/${updatedNote._id}`);
        setNotes(prev => prev.filter(n => n._id !== updatedNote._id));
      } catch (err) {
        console.error('Failed to delete blank note:', err);
      }
      return;
    }

    if (updatedNote.tempId && !updatedNote._id) {
      try {
        const res = await axios.post('http://localhost:5000/api/notes', {
          ...updatedNote,
          tempId: undefined,
        });
        setNotes(prev =>
          prev.map(n =>
            n.tempId === updatedNote.tempId ? res.data : n
          )
        );
      } catch (err) {
        console.error('Failed to save new note:', err);
      }
      return;
    }

    if (updatedNote._id) {
      try {
        const res = await axios.put(
          `http://localhost:5000/api/notes/${updatedNote._id}`,
          updatedNote
        );
        setNotes(prev =>
          prev.map(n => n._id === updatedNote._id ? res.data : n)
        );

        const noteLabels = updatedNote.labels || [];
        const currentLabelNames = labels.map(l => l.name.toLowerCase());
        const newLabels = noteLabels.filter(
          l => !currentLabelNames.includes(l.toLowerCase())
        );

        if (newLabels.length > 0) {
          const addedLabels = await Promise.all(
            newLabels.map(name =>
              axios.post('http://localhost:5000/api/labels', { name }).then(res => res.data)
            )
          );
          setLabels(prev =>
            [...prev, ...addedLabels].sort((a, b) =>
              a.name.localeCompare(b.name)
            )
          );
        }
      } catch (err) {
        console.error('Error updating note:', err);
      }
    }
  };

  const togglePin = async (id) => {
    const noteToToggle = notes.find((n) => n._id === id);
    if (!noteToToggle) return;
    const updated = { ...noteToToggle, pinned: !noteToToggle.pinned };
    await updateNote(updated);
  };

  const filterByLabel = (note) => {
    if (activeLabel === 'Trash') return note.trashed;
    return (activeLabel === 'Notes' || !activeLabel || activeLabel === 'Labels')
      ? !note.trashed
      : note.labels?.includes(activeLabel) && !note.trashed;
  };

  const addTemporaryNote = () => {
    const hasEmpty = notes.some(n => !n.content?.trim() && !n._id);
    if (hasEmpty) {
      alert('Please fill or delete the empty note before adding a new one.');
      return;
    }

    const hasExistingEmpty = notes.some(n => !n.content?.trim() && n._id && !n.trashed);
    if (hasExistingEmpty) {
      alert('Please fill or delete the existing empty note before adding a new one.');
      return;
    }

    const tempNote = {
      tempId: `temp-${Date.now()}`,
      content: '',
      x: Math.floor(Math.random() * 600),
      y: Math.floor(Math.random() * 400),
      color: '#ffff88',
      font: 'sans-serif',
      pinned: false,
      labels: [],
      trashed: false
    };
    setNotes(prev => [...prev, tempNote]);
  };

  const handleDelete = async (note) => {
    const isBlank = !note.content?.trim();
    if (isBlank) {
      if (note._id) {
        await axios.delete(`http://localhost:5000/api/notes/${note._id}`);
        setNotes(prev => prev.filter(n => n._id !== note._id));
      } else {
        setNotes(prev => prev.filter(n => n.tempId !== note.tempId));
      }
    } else {
      if (note._id) {
        const res = await axios.put(`http://localhost:5000/api/notes/${note._id}`, { ...note, trashed: true });
        setNotes(prev => prev.map(n => n._id === note._id ? res.data : n));
      }
    }
  };

  const handleRestore = async (note) => {
    if (note._id) {
      const res = await axios.put(`http://localhost:5000/api/notes/${note._id}`, { ...note, trashed: false });
      setNotes(prev => prev.map(n => n._id === note._id ? res.data : n));
    }
  };

  const handlePermanentDelete = async (note) => {
    if (note._id) {
      await axios.delete(`http://localhost:5000/api/notes/${note._id}`);
      setNotes(prev => prev.filter(n => n._id !== note._id));
    }
  };

  const filteredNotes = notes.filter(filterByLabel);
  const pinnedNotes = filteredNotes.filter(note => note.pinned);
  const unpinnedNotes = filteredNotes.filter(note => !note.pinned);

  return (
    <div className="canvas-section">
      <div className="canvas-notes">
        {[...pinnedNotes, ...unpinnedNotes].map((note) => (
          <Note
            key={note._id || note.tempId}
            note={note}
            onDelete={() => handleDelete(note)}
            onPin={togglePin}
            onUpdate={updateNote}
            onRestore={handleRestore}
            onPermanentDelete={handlePermanentDelete}
            isInTrash={activeLabel === 'Trash'}
          />
        ))}
      </div>
      {activeLabel !== 'Trash' && (
        <button className="add-note-btn" onClick={addTemporaryNote} title="Add new note">
          ï¼‹
        </button>
      )}
    </div>
  );
};

export default NoteCanvas;
