import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Sidebar.css';

const Sidebar = ({ activeLabel, setActiveLabel, toggleLabels }) => {
  const [availableLabels, setAvailableLabels] = useState([]);

  const menuItems = ['Notes', 'Reminders', 'Archive', 'Trash'];

  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then((res) => {
        const allLabels = res.data.flatMap(note => note.labels || []);
        const uniqueLabels = [...new Set(allLabels)];
        setAvailableLabels(uniqueLabels);
      })
      .catch(err => console.error(err));
  }, []);

  const handleLabelClick = (label) => {
    setActiveLabel(label);
    toggleLabels(label === 'Labels'); // show label manager if clicked
  };

  return (
    <div className="sidebar">
      <ul className="sidebar-menu">
        {menuItems.map((item) => (
          <li
            key={item}
            className={activeLabel === item ? 'active' : ''}
            onClick={() => handleLabelClick(item)}
          >
            {item === 'Notes' && 'üìÅ '}
            {item === 'Reminders' && 'üîî '}
            {item === 'Archive' && 'üì¶ '}
            {item === 'Trash' && 'üóëÔ∏è '}
            {item}
          </li>
        ))}
      </ul>

      <hr />

      <div className="label-section">
        <h4
          className={activeLabel === 'Labels' ? 'active' : ''}
          onClick={() => handleLabelClick('Labels')}
          style={{ cursor: 'pointer' }}
        >
          üè∑Ô∏è Manage Labels
        </h4>

        {availableLabels.length > 0 && (
          <ul className="label-list">
            {availableLabels.map((label) => (
              <li
                key={label}
                className={activeLabel === label ? 'active' : ''}
                onClick={() => handleLabelClick(label)}
              >
                üè∑Ô∏è {label}
              </li>
            ))}
          </ul>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
