import React from 'react';
import './Note.css';

const Note = ({ note, onDelete, onDragEnd }) => {
  const handleDragEnd = (e) => {
    const newX = e.clientX;
    const newY = e.clientY;
    onDragEnd(note._id, newX, newY);
  };

  return (
    <div
      className="note"
      style={{
        left: note.x,
        top: note.y,
        backgroundColor: note.color,
        fontFamily: note.font,
      }}
      draggable
      onDragEnd={handleDragEnd}
    >
      <p>{note.content}</p>
      <button className="delete-btn" onClick={() => onDelete(note._id)}>üóëÔ∏è</button>
    </div>
  );
};

export default Note;
