import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Note from './Note';
import './NoteCanvas.css';

const NoteCanvas = ({ activeLabel, onAddNote }) => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      setNotes(prev => prev.filter(note => note._id !== id));
    } catch (err) {
      console.error('Error deleting note:', err);
    }
  };

  const updateNote = async (updatedNote) => {
    try {
      const res = await axios.put(`http://localhost:5000/api/notes/${updatedNote._id}`, updatedNote);
      setNotes(prev =>
        prev.map((n) => (n._id === updatedNote._id ? res.data : n))
      );
    } catch (err) {
      console.error('Error updating note:', err);
    }
  };

  const togglePin = async (id) => {
    const noteToToggle = notes.find((n) => n._id === id);
    if (!noteToToggle) return;

    const updated = { ...noteToToggle, pinned: !noteToToggle.pinned };
    await updateNote(updated);
  };

  const filterByLabel = (note) => {
    return activeLabel === 'Notes' || !activeLabel || activeLabel === 'Labels'
      ? true
      : note.labels?.includes(activeLabel);
  };

  const filteredNotes = notes.filter(filterByLabel);
  const pinnedNotes = filteredNotes.filter(note => note.pinned);
  const unpinnedNotes = filteredNotes.filter(note => !note.pinned);

  return (
    <div className="canvas-section">
      <div className="canvas-notes">
        {[...pinnedNotes, ...unpinnedNotes].map((note) => (
          <Note
            key={note._id}
            note={note}
            onDelete={deleteNote}
            onPin={togglePin}
            onUpdate={updateNote}
          />
        ))}
      </div>
      <button className="add-note-btn" onClick={onAddNote} title="Add new note">
        ï¼‹
      </button>
    </div>
  );
};

export default NoteCanvas;
