import React, { useState, useRef, useEffect } from 'react';
import './Note.css';

const Note = ({ note, onDelete, onPin, onUpdate }) => {
  const [showLabelInput, setShowLabelInput] = useState(false);
  const [newLabel, setNewLabel] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState(note.content || '');
  const [showColorPalette, setShowColorPalette] = useState(false);
  const [showFontToolbar, setShowFontToolbar] = useState(false);
  const [fontStyle, setFontStyle] = useState({
    heading: 'normal', bold: false, italic: false, underline: false, strike: false
  });

  const noteRef = useRef(null);
  const textareaRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (e) => {
      if (noteRef.current && !noteRef.current.contains(e.target)) {
        setShowFontToolbar(false);
        setShowColorPalette(false);
        if (isEditing) handleContentUpdate();
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isEditing]);

  useEffect(() => {
    if (isEditing && textareaRef.current) {
      textareaRef.current.focus();
    }
  }, [isEditing]);

  const applyStyles = () => {
    const styles = {};
    if (fontStyle.bold) styles.fontWeight = 'bold';
    if (fontStyle.italic) styles.fontStyle = 'italic';
    styles.textDecoration = `${fontStyle.underline ? 'underline ' : ''}${fontStyle.strike ? 'line-through' : ''}`.trim();
    if (fontStyle.heading === 'h1') styles.fontSize = '20px';
    else if (fontStyle.heading === 'h2') styles.fontSize = '16px';
    else styles.fontSize = '14px';
    return styles;
  };

  const handleAddLabel = () => {
    const trimmed = newLabel.trim();
    if (!trimmed) return;
    onUpdate({ ...note, labels: [...(note.labels || []), trimmed], _touched: true });
    setNewLabel('');
    setShowLabelInput(false);
  };

  const handleRemoveLabel = (labelToRemove) => {
    const updatedLabels = note.labels.filter(label => label !== labelToRemove);
    onUpdate({ ...note, labels: updatedLabels, _touched: true });
  };

  const handleContentUpdate = () => {
    setIsEditing(false);
    onUpdate({ ...note, content: editedContent, _touched: true });
  };

  const handleDragEnd = (e) => {
    if (note.deleted) return;
    const canvas = document.querySelector('.note-canvas');
    if (!canvas || !noteRef.current) return;
    const noteEl = noteRef.current;
    const sidebar = document.querySelector('.sidebar');
    const topbar = document.querySelector('.topbar');
    const canvasRect = canvas.getBoundingClientRect();
    const noteWidth = noteEl.offsetWidth;
    const noteHeight = noteEl.offsetHeight;
    const sidebarWidth = sidebar && getComputedStyle(sidebar).display !== 'none' ? sidebar.offsetWidth : 0;
    const topbarHeight = topbar ? topbar.offsetHeight : 0;
    const margin = 10;
    let newX = e.clientX - canvasRect.left - noteWidth / 2;
    let newY = e.clientY - canvasRect.top - noteHeight / 2;
    const canvasWidth = canvas.offsetWidth;
    const canvasHeight = canvas.offsetHeight;
    const clampedX = Math.max(sidebarWidth + margin, Math.min(newX, canvasWidth - noteWidth - margin));
    const clampedY = Math.max(topbarHeight + margin, Math.min(newY, canvasHeight - noteHeight - margin));
    onUpdate({ ...note, x: clampedX, y: clampedY, _touched: true });
  };

  const toggleStyle = (type) => {
    setFontStyle(prev => ({ ...prev, [type]: !prev[type] }));
  };

  const setHeading = (type) => {
    setFontStyle(prev => ({ ...prev, heading: type }));
  };

  return (
    <div
      className={`note${note.deleted ? ' deleted' : ''}`}
      style={{ backgroundColor: note.color || '#fff', ...applyStyles(), left: note.x, top: note.y, position: 'absolute' }}
      draggable={!note.deleted}
      onDragEnd={handleDragEnd}
      ref={noteRef}
    >
      <div className="note-content">
        {note.deleted ? (
          <p>{note.content?.length ? note.content : <i className="placeholder">Empty note</i>}</p>
        ) : isEditing ? (
          <textarea
            ref={textareaRef}
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
            placeholder="Write your note..."
          />
        ) : (
          <p onClick={() => setIsEditing(true)}>{note.content?.length ? note.content : <i className="placeholder">Click to edit...</i>}</p>
        )}
      </div>

      {note.labels?.length > 0 && (
        <div className="note-labels">
          {note.labels.map(label => (
            <span className="note-label" key={label}>
              {label}
              {!note.deleted && <button onClick={() => handleRemoveLabel(label)}>√ó</button>}
            </span>
          ))}
        </div>
      )}

      {!note.deleted && showLabelInput && (
        <div className="note-label-input">
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Add label"
          />
          <button onClick={handleAddLabel}>Add</button>
        </div>
      )}

      {!note.deleted && (
        <div className="note-tools">
          <button className="toolbar-icon" onClick={() => setShowFontToolbar(p => !p)}>üÖ∞Ô∏è</button>
          <button className="toolbar-icon" onClick={() => setShowColorPalette(p => !p)}>üé®</button>

          {showFontToolbar && (
            <div className="font-toolbar">
              <button onClick={() => setHeading('normal')}>Aa</button>
              <button onClick={() => setHeading('h1')}>H1</button>
              <button onClick={() => setHeading('h2')}>H2</button>
              <button onClick={() => toggleStyle('bold')}>B</button>
              <button onClick={() => toggleStyle('italic')}>I</button>
              <button onClick={() => toggleStyle('underline')}>U</button>
              <button onClick={() => toggleStyle('strike')}>S</button>
            </div>
          )}

          {showColorPalette && (
            <div className="color-palette">
              {['#fff475', '#f28b82', '#fbbc04', '#ccff90', '#a7ffeb', '#cbf0f8', '#aecbfa', '#d7aefb', '#fdcfe8'].map((color, index) => (
                <div
                  key={index}
                  className="color-swatch"
                  style={{ backgroundColor: color }}
                  onClick={() => onUpdate({ ...note, color, _touched: true })}
                />
              ))}
            </div>
          )}
        </div>
      )}

      <div className="note-actions">
        {note.deleted ? (
          <>
            <button onClick={() => onUpdate({ ...note, deleted: false })}>‚ôªÔ∏è Restore</button>
            <button onClick={() => onDelete(note, true)}>‚ùå Delete</button>
          </>
        ) : (
          <>
            <button onClick={() => onPin(note._id)} title="Toggle Pin">{note.pinned ? 'üìå' : 'üìç'}</button>
            <button
              onClick={() => {
                if (!note.labels || note.labels.length === 0) {
                  setShowLabelInput(prev => !prev);
                }
              }}
              title={note.labels?.length > 0 ? 'Remove label first' : 'Add label'}
              disabled={note.labels?.length > 0}
            >
              üè∑Ô∏è
            </button>
            <button onClick={() => onDelete(note)} title="Delete">üóëÔ∏è</button>
          </>
        )}
      </div>
    </div>
  );
};

export default Note;
