import React, { useEffect, useState } from 'react';
import Draggable from 'react-draggable';
import './App.css';

const COLORS = ['#FFFB7D', '#FFC1C1', '#B9FBC0', '#A0C4FF', '#FFD6A5'];
const FONTS = ['Arial', 'Comic Sans MS', 'Georgia', 'Courier New', 'Trebuchet MS'];
const PINS = ['üìå', 'üìç', 'üìé', '‚úÇÔ∏è', 'üñáÔ∏è'];

function Note({ note, onUpdate, onDelete }) {
  const [content, setContent] = useState(note.content);
  const [color, setColor] = useState(note.color);
  const [font, setFont] = useState(note.font);
  const [pin, setPin] = useState(note.pin);
  const [position, setPosition] = useState(note.position || { x: 0, y: 0 });

  useEffect(() => {
    const timeout = setTimeout(() => {
      onUpdate(note._id, { content, color, font, pin, position });
    }, 500);
    return () => clearTimeout(timeout);
  }, [content, color, font, pin, position]);

  const handleDragStop = (e, data) => {
    const newPos = { x: data.x, y: data.y };
    setPosition(newPos);
    onUpdate(note._id, { ...note, position: newPos });
  };

  return (
    <Draggable defaultPosition={position} onStop={handleDragStop}>
      <div className="note" style={{ backgroundColor: color, fontFamily: font }}>
        <div className="note-toolbar">
          <select value={color} onChange={e => setColor(e.target.value)} title="Color">
            {COLORS.map(c => <option key={c} value={c}>{c}</option>)}
          </select>
          <select value={font} onChange={e => setFont(e.target.value)} title="Font">
            {FONTS.map(f => <option key={f} value={f}>{f}</option>)}
          </select>
          <select value={pin} onChange={e => setPin(e.target.value)} title="Pin">
            {PINS.map(p => <option key={p} value={p}>{p}</option>)}
          </select>
          <button onClick={() => onDelete(note._id)} title="Delete">‚ùå</button>
        </div>
        <textarea
          value={content}
          onChange={e => setContent(e.target.value)}
          placeholder="Write something..."
        />
        <div className="note-pin">{pin}</div>
      </div>
    </Draggable>
  );
}

export default Note;
