import React, { useState } from 'react';
import './Note.css';

const Note = ({ note, onDelete, onPin, onUpdate }) => {
  const [showLabelInput, setShowLabelInput] = useState(false);
  const [newLabel, setNewLabel] = useState('');

  const handleAddLabel = () => {
    const trimmed = newLabel.trim();
    if (!trimmed) return;
    const updatedLabels = note.labels ? [...note.labels, trimmed] : [trimmed];
    const uniqueLabels = [...new Set(updatedLabels)];
    onUpdate({ ...note, labels: uniqueLabels });
    setNewLabel('');
  };

  const handleRemoveLabel = (labelToRemove) => {
    const updatedLabels = note.labels.filter((l) => l !== labelToRemove);
    onUpdate({ ...note, labels: updatedLabels });
  };

  return (
    <div className="note" style={{ backgroundColor: note.color }}>
      <div className="note-content">
        <p>{note.content}</p>
      </div>

      {note.labels?.length > 0 && (
        <div className="note-labels">
          {note.labels.map((label, idx) => (
            <span className="note-label" key={idx}>
              {label}
              <button onClick={() => handleRemoveLabel(label)}>√ó</button>
            </span>
          ))}
        </div>
      )}

      {showLabelInput && (
        <div className="note-label-input">
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Add label"
          />
          <button onClick={handleAddLabel}>Add</button>
        </div>
      )}

      <div className="note-actions">
        <button onClick={() => onPin(note._id)}>
          {note.pinned ? 'Unpin üìå' : 'Pin üìç'}
        </button>
        <button onClick={() => setShowLabelInput((prev) => !prev)}>
          üè∑Ô∏è
        </button>
        <button onClick={() => onDelete(note._id)}>üóëÔ∏è</button>
      </div>
    </div>
  );
};

export default Note;
