import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Note from './Note';
import Topbar from './Topbar';
import './NoteCanvas.css';

const NoteCanvas = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then(res => {
        console.log("Fetched notes:", res.data);
        setNotes(res.data);
      })
      .catch(err => console.error(err));
  }, []);

  const deleteNote = async (id) => {
    await axios.delete(`http://localhost:5000/api/notes/${id}`);
    setNotes(notes.filter(note => note._id !== id));
  };

  const updateNotePosition = async (id, x, y) => {
    await axios.put(`http://localhost:5000/api/notes/${id}`, { x, y });
    setNotes(notes.map(n => n._id === id ? { ...n, x, y } : n));
  };

  const addNewNote = async () => {
    const newNote = {
      content: "New Note...",
      x: Math.floor(Math.random() * 600),
      y: Math.floor(Math.random() * 400),
      color: "#ffff88",
      font: "sans-serif",
      pinned: false
    };

    try {
      const res = await axios.post('http://localhost:5000/api/notes', newNote);
      setNotes([...notes, res.data]);
    } catch (err) {
      console.error("Failed to add note", err);
    }
  };

  const pinnedNotes = notes.filter(note => note.pinned);
  const unpinnedNotes = notes.filter(note => !note.pinned);

  return (
    <div>
      <Topbar onAddNote={addNewNote} />

      <div className="canvas-section">
        {pinnedNotes.length > 0 && (
          <>
            <h3 className="note-section-title">üìå Pinned</h3>
            <div className="canvas">
              {pinnedNotes.map(note => (
                <Note
                  key={note._id}
                  note={note}
                  onDelete={deleteNote}
                  onDragEnd={updateNotePosition}
                />
              ))}
            </div>
          </>
        )}

        {unpinnedNotes.length > 0 && (
          <>
            <h3 className="note-section-title">üìù Others</h3>
            <div className="canvas">
              {unpinnedNotes.map(note => (
                <Note
                  key={note._id}
                  note={note}
                  onDelete={deleteNote}
                  onDragEnd={updateNotePosition}
                />
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default NoteCanvas;
