import React, { useEffect, useState } from 'react';
import Reminder from './Reminder';
import './Reminders.css';
import axios from 'axios';

const Reminders = () => {
  const [reminders, setReminders] = useState([]);
  const [newReminder, setNewReminder] = useState({
    title: '',
    description: '',
    dueDate: '',
  });

  // Fetch reminders on mount
  useEffect(() => {
    axios.get('http://localhost:5000/api/reminders')
      .then(res => setReminders(res.data))
      .catch(err => console.error('Error fetching reminders:', err));
  }, []);

  // Request browser notification permission on mount
  useEffect(() => {
    if (Notification.permission !== 'granted') {
      Notification.requestPermission()
        .then(permission => {
          console.log('Notification permission:', permission);
        });
    }
  }, []);

  const handleChange = (e) => {
    setNewReminder({ ...newReminder, [e.target.name]: e.target.value });
  };

  const addReminder = () => {
    if (!newReminder.title || !newReminder.dueDate)
      return alert('Title and Due Date required');

    axios.post('http://localhost:5000/api/reminders', newReminder)
      .then(res => {
        setReminders([...reminders, res.data]);
        setNewReminder({ title: '', description: '', dueDate: '' });

        // Show in-app notification
        if (Notification.permission === 'granted') {
          new Notification('ðŸ“Œ Reminder Added', {
            body: `${res.data.title} â€” Due: ${new Date(res.data.dueDate).toLocaleString()}`
          });
        }
      })
      .catch(err => console.error('Error adding reminder:', err));
  };

  const deleteReminder = (id) => {
    axios.delete(`http://localhost:5000/api/reminders/${id}`)
      .then(() => {
        setReminders(reminders.filter(rem => rem._id !== id));
      })
      .catch(err => console.error('Error deleting reminder:', err));
  };

  const toggleDone = (id) => {
    const reminder = reminders.find(r => r._id === id);
    if (!reminder) return;

    axios.put(`http://localhost:5000/api/reminders/${id}`, {
      ...reminder,
      isDone: !reminder.isDone
    }).then(res => {
      setReminders(reminders.map(r => (r._id === id ? res.data : r)));
    }).catch(err => console.error('Error updating reminder:', err));
  };

  return (
    <div className="reminders-page">
      <h2>Reminders</h2>

      <div className="reminder-form">
        <input
          type="text"
          name="title"
          placeholder="Reminder title"
          value={newReminder.title}
          onChange={handleChange}
        />
        <input
          type="text"
          name="description"
          placeholder="Description (optional)"
          value={newReminder.description}
          onChange={handleChange}
        />
        <input
          type="datetime-local"
          name="dueDate"
          value={newReminder.dueDate}
          onChange={handleChange}
        />
        <button onClick={addReminder}>Add</button>
      </div>

      <div className="reminder-list">
        {reminders.length === 0 ? (
          <p className="empty-text">No reminders yet. Add one above!</p>
        ) : (
          reminders.map((reminder) => (
            <Reminder
              key={reminder._id}
              reminder={reminder}
              onDelete={deleteReminder}
              onToggleDone={toggleDone}
            />
          ))
        )}
      </div>
    </div>
  );
};

export default Reminders;
