import Draggable from 'react-draggable';

function Note({ note, onDragStop, onChange, onDelete }) {
  return (
    <Draggable
      defaultPosition={{ x: note.x, y: note.y }}
      onStop={(e, data) => onDragStop(note._id, data.x, data.y)}
    >
      <div style={{
        backgroundColor: note.color,
        fontFamily: note.font,
        padding: "10px",
        position: "absolute",
        cursor: "move",
        border: note.pinStyle === "pin" ? "2px solid black" : "none"
      }}>
        <textarea
          value={note.content}
          onChange={(e) => onChange(note._id, e.target.value)}
        />
        <button onClick={() => onDelete(note._id)}>X</button>
      </div>
    </Draggable>
  );
}

export default Note;
