import React, { useState } from 'react';
import './Note.css';

const Note = ({ note, onDelete, onDragEnd }) => {
  const [content, setContent] = useState(note.content);

  const handleBlur = async () => {
    if (content !== note.content) {
      try {
        await fetch(`http://localhost:5000/api/notes/${note._id}`, {
          method: 'PUT',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ content }),
        });
      } catch (error) {
        console.error("Failed to update note:", error);
      }
    }
  };

  return (
    <div
      className="note"
      style={{ left: note.x, top: note.y, backgroundColor: note.color }}
    >
      <textarea
        className="note-content"
        value={content}
        onChange={(e) => setContent(e.target.value)}
        onBlur={handleBlur}
      />
      <button className="delete-btn" onClick={() => onDelete(note._id)}>üóëÔ∏è</button>
    </div>
  );
};

export default Note;
