import React, { useEffect, useRef, useState } from 'react';
import axios from 'axios';
import './LabelManager.css';

const LabelManager = ({ activeLabel, setActiveLabel }) => {
  const [labels, setLabels] = useState([]);
  const [newLabel, setNewLabel] = useState('');
  const modalRef = useRef(null);

  useEffect(() => {
    fetchLabels();

    // Close modal on outside click
    const handleClickOutside = (e) => {
      if (modalRef.current && !modalRef.current.contains(e.target)) {
        setActiveLabel('Notes');
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const fetchLabels = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/labels');
      setLabels(res.data);
    } catch (err) {
      console.error('Error fetching labels:', err);
    }
  };

  const addLabel = async () => {
    if (!newLabel.trim()) return;
    try {
      const res = await axios.post('http://localhost:5000/api/labels', { name: newLabel });
      setLabels([...labels, res.data]);
      setNewLabel('');
    } catch (err) {
      alert('Label already exists or invalid!');
    }
  };

  const deleteLabel = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/api/labels/${id}`);
      setLabels(labels.filter((label) => label._id !== id));
    } catch (err) {
      console.error('Error deleting label:', err);
    }
  };

  return (
    <div className="label-manager-overlay">
      <div className="label-manager-box" ref={modalRef}>
        <h3>Manage Labels üè∑Ô∏è</h3>

        <div className="label-input">
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Enter label name"
          />
          <button onClick={addLabel}>Add</button>
        </div>

        <ul className="label-list">
          {labels.map((label) => (
            <li key={label._id}>
              <span onClick={() => setActiveLabel(label.name)}>{label.name}</span>
              <button onClick={() => deleteLabel(label._id)}>‚ùå</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default LabelManager;
