import React, { useState } from 'react';
import './App.css';

function Sidebar({ categories, activeCategoryId, onSelectCategory, onAddCategory, onDeleteCategory }) {
  const [newCategory, setNewCategory] = useState('');

  const handleAdd = () => {
    if (newCategory.trim() !== '') {
      onAddCategory(newCategory.trim());
      setNewCategory('');
    }
  };

  return (
    <div className="sidebar">
      <h2>ðŸ“‚ Categories</h2>
      <ul>
        {categories.map(cat => (
          <li
            key={cat._id}
            className={activeCategoryId === cat._id ? 'active' : ''}
            onClick={() => onSelectCategory(cat._id)}
          >
            {cat.name}
            <span
              title="Delete"
              onClick={(e) => {
                e.stopPropagation();
                onDeleteCategory(cat._id);
              }}
              style={{ float: 'right', cursor: 'pointer', color: '#ef4444', fontWeight: 'bold' }}
            >
              âœ–
            </span>
          </li>
        ))}
      </ul>

      <input
        type="text"
        placeholder="New category..."
        value={newCategory}
        onChange={e => setNewCategory(e.target.value)}
        style={{
          marginTop: '10px',
          padding: '6px',
          borderRadius: '5px',
          border: '1px solid #ccc',
          fontSize: '14px',
        }}
      />
      <button onClick={handleAdd}>âž• Add</button>
    </div>
  );
}

export default Sidebar;
