import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Draggable from 'react-draggable';

const COLORS = ['#FFFB7D', '#FFC1C1', '#B9FBC0', '#A0C4FF', '#FFD6A5'];
const FONTS = ['Arial', 'Comic Sans MS', 'Georgia', 'Courier New', 'Trebuchet MS'];
const PINS = ['üìå', 'üìç', 'üìé', '‚úÇÔ∏è', 'üñáÔ∏è'];

function Note({ note, onUpdate, onDelete }) {
  const [content, setContent] = useState(note.content);
  const [color, setColor] = useState(note.color);
  const [font, setFont] = useState(note.font);
  const [pin, setPin] = useState(note.pin);
  const [position, setPosition] = useState({ x: note.position.x, y: note.position.y });

  // Autosave changes with debounce to avoid too many calls
  useEffect(() => {
    const timeout = setTimeout(() => {
      onUpdate(note._id, { content, color, font, pin, position });
    }, 700);
    return () => clearTimeout(timeout);
  }, [content, color, font, pin, position]);

  const handleDragStop = (e, data) => {
    setPosition({ x: data.x, y: data.y });
  };

  return (
    <Draggable
      defaultPosition={{ x: position.x, y: position.y }}
      onStop={handleDragStop}
    >
      <div style={{
        width: 220,
        minHeight: 150,
        backgroundColor: color,
        fontFamily: font,
        padding: 15,
        borderRadius: 10,
        boxShadow: '0 5px 10px rgba(0,0,0,0.2)',
        position: 'absolute',
        cursor: 'move',
        userSelect: 'none',
        color: '#333',
      }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', marginBottom: 8 }}>
          <select value={color} onChange={(e) => setColor(e.target.value)} title="Note Color">
            {COLORS.map(c => <option key={c} value={c}>{c}</option>)}
          </select>
          <select value={font} onChange={(e) => setFont(e.target.value)} title="Font Style">
            {FONTS.map(f => <option key={f} value={f}>{f}</option>)}
          </select>
          <select value={pin} onChange={(e) => setPin(e.target.value)} title="Pin Style">
            {PINS.map(p => <option key={p} value={p}>{p}</option>)}
          </select>
          <button onClick={() => onDelete(note._id)} style={{ background: 'none', border: 'none', cursor: 'pointer' }} title="Delete Note">‚ùå</button>
        </div>
        <textarea
          value={content}
          onChange={(e) => setContent(e.target.value)}
          placeholder="Write something..."
          style={{
            width: '100%',
            height: 90,
            border: 'none',
            background: 'transparent',
            resize: 'none',
            fontFamily: font,
            fontSize: 14,
            outline: 'none',
            color: '#333',
          }}
        />
        <div style={{ textAlign: 'right', fontSize: 24 }}>
          {pin}
        </div>
      </div>
    </Draggable>
  );
}

function App() {
  const [notes, setNotes] = useState([]);

  // Fetch all notes
  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then(res => setNotes(res.data))
      .catch(console.error);
  }, []);

  // Create new note
  const addNote = () => {
    axios.post('http://localhost:5000/api/notes', {
      content: '',
      color: '#FFFB7D',
      font: 'Arial',
      pin: 'üìå',
      position: { x: 100, y: 100 }
    }).then(res => {
      setNotes(prev => [...prev, res.data]);
    });
  };

  // Update note
  const updateNote = (id, updatedFields) => {
    axios.put(`http://localhost:5000/api/notes/${id}`, updatedFields)
      .then(res => {
        setNotes(prev => prev.map(note => note._id === id ? res.data : note));
      });
  };

  // Delete note
  const deleteNote = (id) => {
    axios.delete(`http://localhost:5000/api/notes/${id}`)
      .then(() => {
        setNotes(prev => prev.filter(note => note._id !== id));
      });
  };

  return (
    <div style={{
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #89f7fe 0%, #66a6ff 100%)',
      padding: 20,
      fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
      position: 'relative',
      overflow: 'hidden',
    }}>
      <h1 style={{ color: '#fff', textAlign: 'center', marginBottom: 20 }}>üìò Personal Space - Text Notes Tool</h1>
      <button onClick={addNote} style={{
        display: 'block',
        margin: '0 auto 20px auto',
        padding: '10px 20px',
        backgroundColor: '#fff',
        border: 'none',
        borderRadius: 20,
        fontWeight: 'bold',
        cursor: 'pointer',
        boxShadow: '0 5px 15px rgba(255,255,255,0.4)',
        transition: 'background-color 0.3s ease',
      }}
        onMouseEnter={e => e.currentTarget.style.backgroundColor = '#f0f0f0'}
        onMouseLeave={e => e.currentTarget.style.backgroundColor = '#fff'}
      >
        ‚ûï Add New Note
      </button>

      {notes.map(note => (
        <Note key={note._id} note={note} onUpdate={updateNote} onDelete={deleteNote} />
      ))}
    </div>
  );
}

export default App;
