import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import Sidebar from './components/Sidebar';
import CategoryColumn from './components/CategoryColumn';

function App() {
  const [categories, setCategories] = useState(['Journal', 'Work', 'Ideas']);
  const [activeCategory, setActiveCategory] = useState('Journal');
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then(res => setNotes(res.data))
      .catch(console.error);
  }, []);

  const addCategory = () => {
    const name = prompt('Enter new category name');
    if (name && !categories.includes(name)) setCategories([...categories, name]);
  };

  const deleteCategory = (name) => {
    if (window.confirm(`Delete category "${name}" and all its notes?`)) {
      setCategories(categories.filter(c => c !== name));
      setNotes(notes.filter(n => n.category !== name));
    }
  };

  const addNote = (category) => {
    axios.post('http://localhost:5000/api/notes', {
      content: '',
      color: '#FFFB7D',
      font: 'Arial',
      pin: 'ðŸ“Œ',
      position: { x: 0, y: 0 },
      category
    }).then(res => setNotes(prev => [...prev, res.data]));
  };

  const updateNote = (id, updatedFields) => {
    axios.put(`http://localhost:5000/api/notes/${id}`, updatedFields)
      .then(res => {
        setNotes(prev => prev.map(note => note._id === id ? res.data : note));
      });
  };

  const deleteNote = (id) => {
    axios.delete(`http://localhost:5000/api/notes/${id}`)
      .then(() => setNotes(prev => prev.filter(note => note._id !== id)));
  };

  return (
    <div className="app">
      <Sidebar
        categories={categories}
        active={activeCategory}
        onSelect={setActiveCategory}
        onAdd={addCategory}
        onDelete={deleteCategory}
      />
      <main className="main">
        {categories.map(category => (
          <CategoryColumn
            key={category}
            category={category}
            notes={notes.filter(n => n.category === category)}
            onAdd={() => addNote(category)}
            onUpdate={updateNote}
            onDelete={deleteNote}
          />
        ))}
      </main>
    </div>
  );
}

export default App;
