import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './LabelManager.css';

const LabelManager = () => {
  const [labels, setLabels] = useState([]);
  const [newLabel, setNewLabel] = useState('');
  const [editingId, setEditingId] = useState(null);
  const [editingText, setEditingText] = useState('');

  const fetchLabels = async () => {
    const res = await axios.get('http://localhost:5000/api/labels');
    setLabels(res.data);
  };

  useEffect(() => {
    fetchLabels();
  }, []);

  const addLabel = async () => {
    if (!newLabel.trim()) return;
    try {
      await axios.post('http://localhost:5000/api/labels', { name: newLabel });
      setNewLabel('');
      fetchLabels();
    } catch (err) {
      alert("Label already exists or error occurred");
    }
  };

  const deleteLabel = async (id) => {
    await axios.delete(`http://localhost:5000/api/labels/${id}`);
    fetchLabels();
  };

  const updateLabel = async (id) => {
    if (!editingText.trim()) return;
    await axios.put(`http://localhost:5000/api/labels/${id}`, { name: editingText });
    setEditingId(null);
    setEditingText('');
    fetchLabels();
  };

  return (
    <div className="label-manager">
      <h3>Manage Labels</h3>
      <div className="label-input">
        <input
          type="text"
          value={newLabel}
          onChange={(e) => setNewLabel(e.target.value)}
          placeholder="Add new label"
        />
        <button onClick={addLabel}>‚ûï</button>
      </div>
      <ul>
        {labels.map(label => (
          <li key={label._id}>
            {editingId === label._id ? (
              <>
                <input
                  value={editingText}
                  onChange={(e) => setEditingText(e.target.value)}
                />
                <button onClick={() => updateLabel(label._id)}>‚úîÔ∏è</button>
                <button onClick={() => setEditingId(null)}>‚ùå</button>
              </>
            ) : (
              <>
                {label.name}
                <button onClick={() => { setEditingId(label._id); setEditingText(label.name); }}>‚úèÔ∏è</button>
                <button onClick={() => deleteLabel(label._id)}>üóëÔ∏è</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default LabelManager;
