import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Note from './Note';
import './NoteCanvas.css';

const NoteCanvas = ({ activeLabel, onAddNote }) => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    axios
      .get('http://localhost:5000/api/notes')
      .then((res) => setNotes(res.data))
      .catch((err) => console.error(err));
  }, []);

  const deleteNote = async (id) => {
    await axios.delete(`http://localhost:5000/api/notes/${id}`);
    setNotes(notes.filter((note) => note._id !== id));
  };

  const updateNote = async (updatedNote) => {
    await axios.put(`http://localhost:5000/api/notes/${updatedNote._id}`, updatedNote);
    setNotes(notes.map((n) => (n._id === updatedNote._id ? updatedNote : n)));
  };

  const togglePin = (id) => {
    const noteToToggle = notes.find((n) => n._id === id);
    const updated = { ...noteToToggle, pinned: !noteToToggle.pinned };
    updateNote(updated);
  };

  const filteredNotes = notes.filter(note => {
    if (activeLabel === 'Notes' || !activeLabel || activeLabel === 'Labels') {
      return true; // show all notes
    }
    return note.labels?.includes(activeLabel); // show notes with the selected label
  });

  const pinnedNotes = filteredNotes.filter(note => note.pinned);
  const unpinnedNotes = filteredNotes.filter(note => !note.pinned);

  return (
    <div className="canvas-section">
      <div className="canvas-notes">
        {[...pinnedNotes, ...unpinnedNotes].map((note) => (
          <Note
            key={note._id}
            note={note}
            onDelete={deleteNote}
            onPin={togglePin}
            onUpdate={updateNote}
          />
        ))}
      </div>
      <button className="add-note-btn" onClick={onAddNote}>ï¼‹</button>
    </div>
  );
};

export default NoteCanvas;
