import React from 'react';
import './Sidebar.css';

const Sidebar = ({ activeLabel, setActiveLabel, toggleLabels, labels }) => {
  const menuItems = [
    { label: 'Notes', icon: 'üìÅ' },
    { label: 'Reminders', icon: 'üîî' },
    { label: 'Trash', icon: 'üóëÔ∏è' },
  ];

  const handleClick = (label) => {
    setActiveLabel(label);
    toggleLabels(label === 'Labels');
  };

  return (
    <aside className="sidebar" role="complementary">
      <nav className="sidebar-menu" role="navigation">
        <ul>
          {menuItems.map(({ label, icon }) => (
            <li key={label}>
              <button
                className={`sidebar-btn ${activeLabel === label ? 'active' : ''}`}
                onClick={() => handleClick(label)}
                aria-pressed={activeLabel === label}
              >
                {icon} {label}
              </button>
            </li>
          ))}
        </ul>
      </nav>

      <hr />

      <div className="label-section">
        <h4
          className={`label-header ${activeLabel === 'Labels' ? 'active' : ''}`}
          onClick={() => handleClick('Labels')}
        >
          üè∑Ô∏è Manage Labels
        </h4>

        {labels.length > 0 && (
          <ul className="label-list">
            {labels.map((label) => (
              <li key={label._id || label.name}>
                <button
                  className={`label-btn ${activeLabel === label.name ? 'active' : ''}`}
                  onClick={() => handleClick(label.name)}
                  aria-pressed={activeLabel === label.name}
                >
                  üè∑Ô∏è {label.name}
                </button>
              </li>
            ))}
          </ul>
        )}
      </div>
    </aside>
  );
};

export default Sidebar;
