import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { DragDropContext, Droppable, Draggable } from '@hello-pangea/dnd';
import { v4 as uuidv4 } from 'uuid';
import Picker from 'emoji-picker-react';
import { HexColorPicker } from 'react-colorful';
import './App.css';

const defaultCategories = ['Thoughts', 'Reminders', 'Journal'];

function App() {
  const [notes, setNotes] = useState([]);
  const [categories, setCategories] = useState(defaultCategories);
  const [showEmojiPicker, setShowEmojiPicker] = useState(false);
  const [selectedNoteId, setSelectedNoteId] = useState(null);
  const [colorPickerVisible, setColorPickerVisible] = useState(false);
  const [fontOptions] = useState(['Arial', 'Comic Sans MS', 'Courier New', 'Georgia', 'Times New Roman']);

  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then(res => setNotes(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleAddNote = (category) => {
    const newNote = {
      content: '',
      category,
      type: category.toLowerCase(),
      position: { x: 0, y: 0 },
      color: '#FFF9C4',
      font: 'Arial',
      pin: 'üìå'
    };
    axios.post('http://localhost:5000/api/notes', newNote)
      .then(res => setNotes([...notes, res.data]))
      .catch(err => console.error(err));
  };

  const handleDeleteNote = (id) => {
    axios.delete(`http://localhost:5000/api/notes/${id}`)
      .then(() => setNotes(notes.filter(note => note._id !== id)))
      .catch(err => console.error(err));
  };

  const handleDragEnd = result => {
    if (!result.destination) return;
    const updatedNotes = [...notes];
    const draggedNote = updatedNotes.find(n => n._id === result.draggableId);
    draggedNote.category = result.destination.droppableId;
    axios.put(`http://localhost:5000/api/notes/${draggedNote._id}`, draggedNote)
      .then(() => setNotes(updatedNotes))
      .catch(err => console.error(err));
  };

  const handleEmojiClick = (emojiObject) => {
    const updatedNotes = notes.map(note => {
      if (note._id === selectedNoteId) {
        return { ...note, pin: emojiObject.emoji };
      }
      return note;
    });
    setNotes(updatedNotes);
    axios.put(`http://localhost:5000/api/notes/${selectedNoteId}`, updatedNotes.find(n => n._id === selectedNoteId))
      .catch(err => console.error(err));
    setShowEmojiPicker(false);
  };

  const handleColorChange = (color) => {
    const updatedNotes = notes.map(note => {
      if (note._id === selectedNoteId) {
        return { ...note, color };
      }
      return note;
    });
    setNotes(updatedNotes);
    axios.put(`http://localhost:5000/api/notes/${selectedNoteId}`, updatedNotes.find(n => n._id === selectedNoteId))
      .catch(err => console.error(err));
  };

  const handleFontChange = (e, noteId) => {
    const updatedNotes = notes.map(note => {
      if (note._id === noteId) {
        return { ...note, font: e.target.value };
      }
      return note;
    });
    setNotes(updatedNotes);
    axios.put(`http://localhost:5000/api/notes/${noteId}`, updatedNotes.find(n => n._id === noteId))
      .catch(err => console.error(err));
  };

  return (
    <div className="app">
      <div className="menu-bar">
        {categories.map(cat => (
          <button key={cat} onClick={() => handleAddNote(cat)}>‚ûï {cat}</button>
        ))}
      </div>

      <DragDropContext onDragEnd={handleDragEnd}>
        <div className="columns">
          {categories.map(cat => (
            <Droppable droppableId={cat} key={cat}>
              {(provided) => (
                <div className="column" ref={provided.innerRef} {...provided.droppableProps}>
                  <h2>{cat}</h2>
                  {notes
                    .filter(note => note.category === cat)
                    .map((note, index) => (
                      <Draggable draggableId={note._id} index={index} key={note._id}>
                        {(provided) => (
                          <div
                            className="note"
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            ref={provided.innerRef}
                            style={{
                              backgroundColor: note.color,
                              fontFamily: note.font,
                              ...provided.draggableProps.style
                            }}
                          >
                            <div className="note-header">
                              <span onClick={() => {
                                setSelectedNoteId(note._id);
                                setShowEmojiPicker(!showEmojiPicker);
                              }}>{note.pin}</span>
                              <select value={note.font} onChange={(e) => handleFontChange(e, note._id)}>
                                {fontOptions.map(font => (
                                  <option key={font} value={font}>{font}</option>
                                ))}
                              </select>
                              <button onClick={() => {
                                setSelectedNoteId(note._id);
                                setColorPickerVisible(!colorPickerVisible);
                              }}>üé®</button>
                              <button onClick={() => handleDeleteNote(note._id)}>üóëÔ∏è</button>
                            </div>
                            <textarea
                              value={note.content}
                              onChange={(e) => {
                                const updatedNote = { ...note, content: e.target.value };
                                setNotes(notes.map(n => (n._id === note._id ? updatedNote : n)));
                                axios.put(`http://localhost:5000/api/notes/${note._id}`, updatedNote)
                                  .catch(err => console.error(err));
                              }}
                            />
                            {showEmojiPicker && selectedNoteId === note._id && (
                              <div className="emoji-picker"><Picker onEmojiClick={handleEmojiClick} /></div>
                            )}
                            {colorPickerVisible && selectedNoteId === note._id && (
                              <div className="color-picker"><HexColorPicker color={note.color} onChange={handleColorChange} /></div>
                            )}
                          </div>
                        )}
                      </Draggable>
                    ))}
                  {provided.placeholder}
                </div>
              )}
            </Droppable>
          ))}
        </div>
      </DragDropContext>
    </div>
  );
}

export default App;
