import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Note from './Note';
import './NoteCanvas.css';

const NoteCanvas = () => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
  axios.get('http://localhost:5000/api/notes')
    .then(res => {
      console.log("Fetched notes:", res.data); // ✅ Add this
      setNotes(res.data);
    })
    .catch(err => console.error(err));
}, []);


  const deleteNote = async (id) => {
    await axios.delete(`http://localhost:5000/api/notes/${id}`);
    setNotes(notes.filter(note => note._id !== id));
  };

  const updateNotePosition = async (id, x, y) => {
    await axios.put(`http://localhost:5000/api/notes/${id}`, { x, y });
    setNotes(notes.map(n => n._id === id ? { ...n, x, y } : n));
  };

  return (
    <div className="canvas">
      {notes.map(note => (
        <Note
          key={note._id}
          note={note}
          onDelete={deleteNote}
          onDragEnd={updateNotePosition}
        />
      ))}
    </div>
  );
};

export default NoteCanvas;
