import React, { useEffect, useState } from "react";
import axios from "axios";
import Draggable from "react-draggable";
import "./App.css";

const defaultStyles = {
  color: "#fffacd",
  font: "Arial",
  pin: "📌",
};

const Note = ({ note, onDragStop, onDelete, onUpdate }) => {
  const handleDrag = (e, data) => {
    onDragStop(note._id, data.x, data.y);
  };

  return (
    <Draggable defaultPosition={{ x: note.x, y: note.y }} onStop={handleDrag}>
      <div className="note" style={{ backgroundColor: note.color, fontFamily: note.font }}>
        <div className="note-header">
          <span>{note.pin}</span>
          <button onClick={() => onDelete(note._id)}>✖</button>
        </div>
        <textarea
          value={note.content}
          onChange={(e) => onUpdate(note._id, { content: e.target.value })}
        />
      </div>
    </Draggable>
  );
};

function App() {
  const [notes, setNotes] = useState([]);
  const [newNoteData, setNewNoteData] = useState({ ...defaultStyles, content: "" });

  useEffect(() => {
    axios.get("http://localhost:5000/api/notes").then((res) => setNotes(res.data));
  }, []);

  const createNote = () => {
    axios.post("http://localhost:5000/api/notes", {
      ...newNoteData,
      x: 100,
      y: 100,
    }).then((res) => {
      setNotes([...notes, res.data]);
      setNewNoteData({ ...defaultStyles, content: "" });
    });
  };

  const updateNote = (id, updates) => {
    const updated = notes.map((note) =>
      note._id === id ? { ...note, ...updates } : note
    );
    setNotes(updated);
    axios.put(`http://localhost:5000/api/notes/${id}`, updates);
  };

  const deleteNote = (id) => {
    setNotes(notes.filter((note) => note._id !== id));
    axios.delete(`http://localhost:5000/api/notes/${id}`);
  };

  const handleDragStop = (id, x, y) => {
    updateNote(id, { x, y });
  };

  return (
    <div className="app">
      <h1>Sticky Notes</h1>
      <div className="controls">
        <textarea
          placeholder="Write something..."
          value={newNoteData.content}
          onChange={(e) => setNewNoteData({ ...newNoteData, content: e.target.value })}
        />
        <select onChange={(e) => setNewNoteData({ ...newNoteData, color: e.target.value })}>
          <option value="#fffacd">Yellow</option>
          <option value="#add8e6">Blue</option>
          <option value="#90ee90">Green</option>
        </select>
        <select onChange={(e) => setNewNoteData({ ...newNoteData, font: e.target.value })}>
          <option value="Arial">Arial</option>
          <option value="Courier New">Courier</option>
          <option value="Times New Roman">Times</option>
        </select>
        <select onChange={(e) => setNewNoteData({ ...newNoteData, pin: e.target.value })}>
          <option value="📌">📌</option>
          <option value="📍">📍</option>
          <option value="📎">📎</option>
        </select>
        <button onClick={createNote}>Add Note</button>
      </div>
      {notes.map((note) => (
        <Note
          key={note._id}
          note={note}
          onDragStop={handleDragStop}
          onDelete={deleteNote}
          onUpdate={updateNote}
        />
      ))}
    </div>
  );
}

export default App;
