import React from 'react';
import Draggable from 'react-draggable';
import './App.css';

const COLORS = ['#FFFB7D', '#FFC1C1', '#B9FBC0', '#A0C4FF', '#FFD6A5'];
const FONTS = ['Arial', 'Comic Sans MS', 'Georgia', 'Courier New', 'Trebuchet MS'];
const PINS = ['üìå', 'üìç', 'üìé', '‚úÇÔ∏è', 'üñáÔ∏è'];

function CategoryColumn({ category, notes, onAddNote, onUpdateNote, onDeleteNote }) {
  const handleAddNote = () => {
    onAddNote(category._id);
  };

  return (
    <div className="category-column">
      <div className="category-header">
        <h3>{category.name}</h3>
        <button onClick={handleAddNote} title="Add note">‚ûï</button>
      </div>

      <div className="notes-area">
        {notes.map(note => (
          <Draggable
            key={note._id}
            defaultPosition={{ x: note.position?.x || 0, y: note.position?.y || 0 }}
            onStop={(e, data) => {
              onUpdateNote(note._id, {
                ...note,
                position: { x: data.x, y: data.y }
              });
            }}
          >
            <div
              className="note"
              style={{ backgroundColor: note.color, fontFamily: note.font }}
            >
              <div className="note-toolbar">
                <select
                  value={note.color}
                  onChange={(e) => onUpdateNote(note._id, { ...note, color: e.target.value })}
                >
                  {COLORS.map(c => <option key={c} value={c}>{c}</option>)}
                </select>
                <select
                  value={note.font}
                  onChange={(e) => onUpdateNote(note._id, { ...note, font: e.target.value })}
                >
                  {FONTS.map(f => <option key={f} value={f}>{f}</option>)}
                </select>
                <select
                  value={note.pin}
                  onChange={(e) => onUpdateNote(note._id, { ...note, pin: e.target.value })}
                >
                  {PINS.map(p => <option key={p} value={p}>{p}</option>)}
                </select>
                <button onClick={() => onDeleteNote(note._id)} title="Delete note">‚ùå</button>
              </div>

              <textarea
                value={note.content}
                onChange={(e) => onUpdateNote(note._id, { ...note, content: e.target.value })}
                placeholder="Write something..."
              />
              <div className="note-pin">{note.pin}</div>
            </div>
          </Draggable>
        ))}
      </div>
    </div>
  );
}

export default CategoryColumn;
