import React, { useState, useRef, useEffect } from 'react';
import './Note.css';
const fonts = [
  { name: '', label: 'abcd', style: 'inherit' },
  { name: 'Arial', label: 'abcd', style: 'Arial, sans-serif' },
  { name: 'Courier New', label: 'abcd', style: '"Courier New", monospace' },
  { name: 'Times New Roman', label: 'abcd', style: '"Times New Roman", serif' },
  { name: 'Georgia', label: 'abcd', style: 'Georgia, serif' },
];
const colorOptions = ['#fff475', '#f28b82', '#fbbc04', '#ccff90', '#a7ffeb', '#cbf0f8', '#aecbfa', '#d7aefb', '#fdcfe8'];
const Note = ({ note, onDelete, onPin, onUpdate }) => {
  const [showLabelInput, setShowLabelInput] = useState(false);
  const [newLabel, setNewLabel] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState(note.content || '');
  const [showColorPalette, setShowColorPalette] = useState(false);
  const noteRef = useRef(null);
  const textareaRef = useRef(null);
  useEffect(() => {
    if (isEditing && textareaRef.current) {
      const len = textareaRef.current.value.length;
      textareaRef.current.setSelectionRange(len, len);
      textareaRef.current.focus();
    }
  }, [isEditing]);
  const handleAddLabel = () => {
    const trimmed = newLabel.trim();
    if (!trimmed) return;
    onUpdate({ ...note, labels: [trimmed], _touched: true });
    setNewLabel('');
    setShowLabelInput(false);
  };
  const handleRemoveLabel = (labelToRemove) => {
    const updatedLabels = note.labels.filter(label => label !== labelToRemove);
    onUpdate({ ...note, labels: updatedLabels, _touched: true });
  };
  const handleContentUpdate = () => {
    setIsEditing(false);
    onUpdate({ ...note, content: editedContent, _touched: true }); // ‚ùó Don't trim
  };
  const handleDragEnd = (e) => {
    const canvas = document.querySelector('.note-canvas');
    if (!canvas || !noteRef.current) return;
    const noteEl = noteRef.current;
    const sidebar = document.querySelector('.sidebar');
    const topbar = document.querySelector('.topbar');
    const canvasRect = canvas.getBoundingClientRect();
    const noteWidth = noteEl.offsetWidth;
    const noteHeight = noteEl.offsetHeight;
    const sidebarVisible = sidebar && window.getComputedStyle(sidebar).display !== 'none';
    const sidebarWidth = sidebarVisible ? sidebar.offsetWidth : 0;
    const topbarHeight = topbar ? topbar.offsetHeight : 0;
    const margin = 10;

    // Cursor positions relative to viewport
    const clientX = e.clientX;
    const clientY = e.clientY;

    // Cursor positions relative to canvas
    let newX = clientX - canvasRect.left - noteWidth / 2;
    let newY = clientY - canvasRect.top - noteHeight / 2;

    const canvasWidth = canvas.offsetWidth;
    const canvasHeight = canvas.offsetHeight;

    // Restrict X based on sidebar and right edge
    const minX = sidebarVisible ? sidebarWidth + margin : margin;
    const maxX = canvasWidth - noteWidth - margin;

    // Restrict Y based on topbar and bottom
    const minY = topbarHeight + margin;
    const maxY = canvasHeight - noteHeight - margin;

    const clampedX = Math.max(minX, Math.min(newX, maxX));
    const clampedY = Math.max(minY, Math.min(newY, maxY));

    onUpdate({ ...note, x: clampedX, y: clampedY, _touched: true });
  };


  return (
    <div
      className="note"
      style={{
        backgroundColor: note.color || '#fff',
        fontFamily: note.font || 'inherit',
        left: note.x,
        top: note.y,
        position: 'absolute',
      }}
      draggable
      onDragEnd={handleDragEnd}
      ref={noteRef}
    >
      <div className="note-content">
        {isEditing ? (
          <textarea
            ref={textareaRef}
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
            onBlur={handleContentUpdate}
            onKeyDown={(e) => {
              if (e.key === 'Enter' && !e.shiftKey) {
                e.preventDefault();
                handleContentUpdate();
              }
            }}
            autoFocus
            placeholder="Write your note..."
          />
        ) : (
          <p onClick={() => setIsEditing(true)}>
            {note.content?.length ? note.content : <i className="placeholder">Click to edit...</i>}
          </p>
        )}
      </div>

      {note.labels?.length > 0 && (
        <div className="note-labels">
          {note.labels.map(label => (
            <span className="note-label" key={label}>
              {label}
              <button onClick={() => handleRemoveLabel(label)}>√ó</button>
            </span>
          ))}
        </div>
      )}

      {showLabelInput && (
        <div className="note-label-input">
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Add label"
          />
          <button onClick={handleAddLabel}>Add</button>
        </div>
      )}

      <div className="note-tools">
        <select
          className="font-dropdown"
          value={note.font || ''}
          onChange={(e) => onUpdate({ ...note, font: e.target.value, _touched: true })}
        >
          {fonts.map((f, index) => (
            <option key={index} value={f.name} style={{ fontFamily: f.style }}>
              {f.label}
            </option>
          ))}
        </select>
        <div className="color-selector">
          <div
            className="color-circle"
            style={{ backgroundColor: note.color || '#fff' }}
            onClick={() => setShowColorPalette(!showColorPalette)}
          />
          {showColorPalette && (
            <div className="color-palette">
              {colorOptions.map((color, index) => (
                <div
                  key={index}
                  className="color-swatch"
                  style={{ backgroundColor: color }}
                  onClick={() => {
                    onUpdate({ ...note, color, _touched: true });
                    setShowColorPalette(false);
                  }}
                />
              ))}
            </div>
          )}
        </div>
      </div>

      <div className="note-actions">
        {note.deleted ? (
          <>
            <button onClick={() => onUpdate({ ...note, deleted: false })}>‚ôªÔ∏è Restore</button>
            <button onClick={() => onDelete(note, true)}>‚ùå Delete</button>
          </>
        ) : (
          <>
            <button onClick={() => onPin(note._id)} title="Toggle Pin">
              {note.pinned ? 'üìå' : 'üìç'}
            </button>
            <button
              onClick={() => {
                if (!note.labels || note.labels.length === 0) {
                  setShowLabelInput(prev => !prev);
                }
              }}
              title={note.labels?.length > 0 ? "Remove label first" : "Add label"}
              disabled={note.labels?.length > 0}
            >
              üè∑Ô∏è
            </button>
            <button onClick={() => onDelete(note)} title="Delete">
              üóëÔ∏è
            </button>
          </>
        )}
      </div>
    </div>
  );
};

export default Note;
