import React, { useState, useRef } from 'react';
import './Note.css';

const Note = ({ note, onDelete, onPin, onUpdate }) => {
  const [showLabelInput, setShowLabelInput] = useState(false);
  const [newLabel, setNewLabel] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [editedContent, setEditedContent] = useState(note.content);
  const noteRef = useRef(null);

  const handleAddLabel = () => {
    const trimmed = newLabel.trim();
    if (!trimmed) return;
    const updatedLabels = note.labels ? [...note.labels, trimmed] : [trimmed];
    const uniqueLabels = [...new Set(updatedLabels)];
    onUpdate({ ...note, labels: uniqueLabels });
    setNewLabel('');
  };

  const handleRemoveLabel = (labelToRemove) => {
    const updatedLabels = note.labels.filter((l) => l !== labelToRemove);
    onUpdate({ ...note, labels: updatedLabels });
  };

  const handleContentUpdate = () => {
    setIsEditing(false);
    if (editedContent !== note.content) {
      onUpdate({ ...note, content: editedContent });
    }
  };

  const handleDragEnd = (e) => {
    const canvas = document.querySelector('.note-canvas');
    const canvasRect = canvas.getBoundingClientRect();
    const newX = e.clientX - canvasRect.left - noteRef.current.offsetWidth / 2;
    const newY = e.clientY - canvasRect.top - noteRef.current.offsetHeight / 2;
    onUpdate({ ...note, x: newX, y: newY });
  };

  return (
    <div
      className="note"
      style={{ backgroundColor: note.color, position: 'absolute', left: note.x, top: note.y }}
      draggable
      onDragEnd={handleDragEnd}
      ref={noteRef}
    >
      <div className="note-content">
        {isEditing ? (
          <textarea
            value={editedContent}
            onChange={(e) => setEditedContent(e.target.value)}
            onBlur={handleContentUpdate}
            onKeyDown={(e) => e.key === 'Enter' && handleContentUpdate()}
            autoFocus
          />
        ) : (
          <p onClick={() => setIsEditing(true)}>{note.content}</p>
        )}
      </div>

      {note.labels?.length > 0 && (
        <div className="note-labels">
          {note.labels.map((label, idx) => (
            <span className="note-label" key={idx}>
              {label}
              <button onClick={() => handleRemoveLabel(label)}>√ó</button>
            </span>
          ))}
        </div>
      )}

      {showLabelInput && (
        <div className="note-label-input">
          <input
            type="text"
            value={newLabel}
            onChange={(e) => setNewLabel(e.target.value)}
            placeholder="Add label"
          />
          <button onClick={handleAddLabel}>Add</button>
        </div>
      )}

      <div className="note-actions">
        <button onClick={() => onPin(note._id)}>
          {note.pinned ? 'Unpin üìå' : 'Pin üìç'}
        </button>
        <button onClick={() => setShowLabelInput((prev) => !prev)}>
          üè∑Ô∏è
        </button>
        <button onClick={() => onDelete(note._id)}>üóëÔ∏è</button>
      </div>
    </div>
  );
};

export default Note;
