import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './Sidebar.css';

const Sidebar = ({ activeLabel, setActiveLabel, toggleLabels }) => {
  const [availableLabels, setAvailableLabels] = useState([]);

  const menuItems = [
    { label: 'Notes', icon: 'üìÅ' },
    { label: 'Reminders', icon: 'üîî' },
    { label: 'Archive', icon: 'üì¶' },
    { label: 'Trash', icon: 'üóëÔ∏è' }
  ];

  useEffect(() => {
    const fetchLabels = async () => {
      try {
        const res = await axios.get('http://localhost:5000/api/notes');
        const allLabels = res.data.flatMap(note => note.labels || []);
        const uniqueLabels = [...new Set(allLabels)];
        setAvailableLabels(uniqueLabels);
      } catch (err) {
        console.error('Error fetching labels:', err);
      }
    };

    fetchLabels();
  }, []);

  const handleLabelClick = (label) => {
    setActiveLabel(label);
    toggleLabels(label === 'Labels');
  };

  return (
    <aside className="sidebar">
      <nav className="sidebar-menu">
        <ul>
          {menuItems.map(({ label, icon }) => (
            <li
              key={label}
              className={activeLabel === label ? 'active' : ''}
              onClick={() => handleLabelClick(label)}
            >
              {icon} {label}
            </li>
          ))}
        </ul>
      </nav>

      <hr />

      <div className="label-section">
        <h4
          className={activeLabel === 'Labels' ? 'active' : ''}
          onClick={() => handleLabelClick('Labels')}
        >
          üè∑Ô∏è Manage Labels
        </h4>

        {availableLabels.length > 0 && (
          <ul className="label-list">
            {availableLabels.map((label) => (
              <li
                key={label}
                className={activeLabel === label ? 'active' : ''}
                onClick={() => handleLabelClick(label)}
              >
                üè∑Ô∏è {label}
              </li>
            ))}
          </ul>
        )}
      </div>
    </aside>
  );
};

export default Sidebar;
