import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Draggable from 'react-draggable';
import './App.css';

const defaultCategories = ['Journal', 'Reminder', 'Thoughts'];

const App = () => {
  const [notes, setNotes] = useState([]);
  const [categories, setCategories] = useState(defaultCategories);
  const [theme, setTheme] = useState(window.matchMedia('(prefers-color-scheme: dark)').matches ? 'dark' : 'light');

  useEffect(() => {
    fetchNotes();
    document.body.className = theme;
  }, [theme]);

  const fetchNotes = async () => {
    try {
      const res = await axios.get('http://localhost:5000/notes');
      setNotes(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  const createNote = async (category) => {
    try {
      const res = await axios.post('http://localhost:5000/notes', {
        category,
        type: category.toLowerCase(),
      });
      setNotes([...notes, res.data]);
    } catch (err) {
      console.error(err);
    }
  };

  const updateNote = async (id, updatedFields) => {
    try {
      const updatedNote = await axios.put(`http://localhost:5000/notes/${id}`, updatedFields);
      setNotes(notes.map(note => note._id === id ? updatedNote.data : note));
    } catch (err) {
      console.error(err);
    }
  };

  const deleteNote = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/notes/${id}`);
      setNotes(notes.filter(note => note._id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  const handleDrag = (e, data, id) => {
    const updatedNote = notes.find(note => note._id === id);
    updateNote(id, { position: { x: data.x, y: data.y } });
  };

  const handleContentChange = (e, id) => {
    const updatedNote = notes.find(note => note._id === id);
    updateNote(id, { content: e.target.value });
  };

  const toggleTheme = () => setTheme(theme === 'light' ? 'dark' : 'light');

  return (
    <div className="app">
      <aside className="sidebar">
        <h2>📘 Personal Space</h2>
        <button onClick={toggleTheme}>Toggle Theme</button>
        {categories.map(cat => (
          <div key={cat} className="category-title">
            {cat}
            <button onClick={() => createNote(cat)}>+</button>
          </div>
        ))}
      </aside>

      <main className="notes-area">
        {categories.map(category => (
          <div key={category} className="category-column">
            <h3>{category}</h3>
            {notes.filter(note => note.category === category).map(note => (
              <Draggable
                key={note._id}
                defaultPosition={note.position}
                onStop={(e, data) => handleDrag(e, data, note._id)}
              >
                <div className="note" style={{ backgroundColor: note.color, fontFamily: note.font }}>
                  <div className="note-header">
                    <span>{note.pin}</span>
                    <button onClick={() => deleteNote(note._id)}>🗑️</button>
                  </div>
                  {note.type === 'reminder' ? (
                    <ul>
                      {note.reminderList.map((item, idx) => (
                        <li key={idx} style={{ textDecoration: item.done ? 'line-through' : 'none' }}>
                          {item.text} ({new Date(item.dueDate).toLocaleDateString()})
                        </li>
                      ))}
                    </ul>
                  ) : (
                    <textarea
                      value={note.content}
                      onChange={(e) => handleContentChange(e, note._id)}
                      placeholder="Write something..."
                    />
                  )}
                </div>
              </Draggable>
            ))}
          </div>
        ))}
      </main>
    </div>
  );
};

export default App;
