import React from 'react';
import './Sidebar.css';
import LabelManager from './LabelManager';

const Sidebar = ({ activeLabel, setActiveLabel }) => {
  const menuItems = ['Notes', 'Reminders', 'Archive', 'Trash'];

  const handleLabelClick = (label) => {
    setActiveLabel(label);
  };

  return (
    <div className="sidebar">
      <h2>‚ò∞ Notes Tool ‚ú®</h2>

      <ul className="sidebar-menu">
        {menuItems.map((item) => (
          <li
            key={item}
            className={activeLabel === item ? 'active' : ''}
            onClick={() => handleLabelClick(item)}
          >
            {item === 'Notes' && 'üìÅ '}
            {item === 'Reminders' && 'üîî '}
            {item === 'Archive' && 'üì¶ '}
            {item === 'Trash' && 'üóëÔ∏è '}
            {item}
          </li>
        ))}
      </ul>

      <hr />

      <div className="label-section">
        <h4
          className={activeLabel === 'Labels' ? 'active' : ''}
          onClick={() => handleLabelClick('Labels')}
          style={{ cursor: 'pointer' }}
        >
          üè∑Ô∏è Labels
        </h4>

        {activeLabel === 'Labels' && (
          <LabelManager activeLabel={activeLabel} setActiveLabel={setActiveLabel} />
        )}
      </div>
    </div>
  );
};

export default Sidebar;
