// src/App.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';
import Sidebar from './components/Sidebar';
import CategoryColumns from './components/CategoryColumn';
import Note from './components/Note';


function App() {
  const [categories, setCategories] = useState(['Journal', 'Work', 'Ideas']);
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:5000/api/notes')
      .then(res => setNotes(res.data))
      .catch(console.error);
  }, []);

  const addCategory = () => {
    const name = prompt('Enter new category name:');
    if (name && !categories.includes(name)) {
      setCategories(prev => [...prev, name]);
    }
  };

  const renameCategory = (oldName) => {
    const newName = prompt(`Rename category '${oldName}' to:`);
    if (newName && newName !== oldName) {
      setCategories(prev =>
        prev.map(c => (c === oldName ? newName : c))
      );
      setNotes(prev =>
        prev.map(n =>
          n.category === oldName ? { ...n, category: newName } : n
        )
      );
    }
  };

  const deleteCategory = (name) => {
    if (window.confirm(`Delete category "${name}" and its notes?`)) {
      setCategories(prev => prev.filter(c => c !== name));
      setNotes(prev => prev.filter(n => n.category !== name));
      // Optionally delete notes from DB too
    }
  };

  const addNote = (category) => {
    axios.post('http://localhost:5000/api/notes', {
      content: '',
      color: '#FFFB7D',
      font: 'Arial',
      pin: 'ðŸ“Œ',
      category,
      position: { x: 0, y: 0 }
    }).then(res => {
      setNotes(prev => [...prev, res.data]);
    });
  };

  const updateNote = (id, updatedFields) => {
    axios.put(`http://localhost:5000/api/notes/${id}`, updatedFields)
      .then(res => {
        setNotes(prev => prev.map(n => n._id === id ? res.data : n));
      });
  };

  const deleteNote = (id) => {
    axios.delete(`http://localhost:5000/api/notes/${id}`)
      .then(() => {
        setNotes(prev => prev.filter(n => n._id !== id));
      });
  };

  return (
    <div className="app-container">
      <Sidebar
        categories={categories}
        addCategory={addCategory}
        renameCategory={renameCategory}
        deleteCategory={deleteCategory}
      />
      <main className="main-content">
        <h1 className="app-title">ðŸ“˜ Personal Space â€“ Text Notes Tool</h1>
        <div className="columns-container">
          {categories.map(cat => (
            <CategoryColumns
              key={cat}
              category={cat}
              notes={notes.filter(n => n.category === cat)}
              addNote={() => addNote(cat)}
              onUpdate={updateNote}
              onDelete={deleteNote}
            />
          ))}
        </div>
      </main>
    </div>
  );
}

export default App;
