import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Note from './Note';
import './NoteCanvas.css';

const NoteCanvas = ({ activeLabel, labels, setLabels }) => {
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    fetchNotes();
  }, []);

  const fetchNotes = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/notes');
      setNotes(res.data);
    } catch (err) {
      console.error('Error fetching notes:', err);
    }
  };

  const deleteNote = async (id) => {
    if (!id) {
      // Temporary frontend-only note
      setNotes(prev => prev.filter(note => !note._id && note.tempId !== id));
      return;
    }

    try {
      await axios.delete(`http://localhost:5000/api/notes/${id}`);
      setNotes(prev => prev.filter(note => note._id !== id));
    } catch (err) {
      console.error('Error deleting note:', err);
    }
  };

  const updateNote = async (updatedNote) => {
    if (!updatedNote._id && updatedNote.content?.trim()) {
      // This is a new note that was just typed into
      try {
        const res = await axios.post('http://localhost:5000/api/notes', {
          ...updatedNote,
          tempId: undefined
        });
        // Replace temporary note with real one from backend
        setNotes(prev =>
          prev.map(n => n.tempId === updatedNote.tempId ? res.data : n)
        );
      } catch (err) {
        console.error('Failed to save new note:', err);
      }
      return;
    }

    if (!updatedNote._id) return; // don't update temp blank notes

    try {
      const res = await axios.put(
        `http://localhost:5000/api/notes/${updatedNote._id}`,
        updatedNote
      );
      setNotes(prev =>
        prev.map(n => n._id === updatedNote._id ? res.data : n)
      );

      // Handle new labels sync
      const noteLabels = updatedNote.labels || [];
      const currentLabelNames = labels.map(l => l.name.toLowerCase());
      const newLabels = noteLabels.filter(l => !currentLabelNames.includes(l.toLowerCase()));

      if (newLabels.length > 0) {
        const addedLabels = await Promise.all(
          newLabels.map(name =>
            axios.post('http://localhost:5000/api/labels', { name }).then(res => res.data)
          )
        );
        setLabels(prev => [...prev, ...addedLabels].sort((a, b) => a.name.localeCompare(b.name)));
      }
    } catch (err) {
      console.error('Error updating note:', err);
    }
  };

  const togglePin = async (id) => {
    const noteToToggle = notes.find((n) => n._id === id);
    if (!noteToToggle) return;

    const updated = { ...noteToToggle, pinned: !noteToToggle.pinned };
    await updateNote(updated);
  };

  const filterByLabel = (note) => {
    return activeLabel === 'Notes' || !activeLabel || activeLabel === 'Labels'
      ? true
      : note.labels?.includes(activeLabel);
  };

  const addTemporaryNote = () => {
    const tempNote = {
      tempId: `temp-${Date.now()}`,
      content: '',
      x: Math.floor(Math.random() * 600),
      y: Math.floor(Math.random() * 400),
      color: '#ffff88',
      font: 'sans-serif',
      pinned: false,
      labels: []
    };
    setNotes(prev => [...prev, tempNote]);
  };

  const filteredNotes = notes.filter(filterByLabel);
  const pinnedNotes = filteredNotes.filter(note => note.pinned);
  const unpinnedNotes = filteredNotes.filter(note => !note.pinned);

  return (
    <div className="canvas-section">
      <div className="canvas-notes">
        {[...pinnedNotes, ...unpinnedNotes].map((note) => (
          <Note
            key={note._id || note.tempId}
            note={note}
            onDelete={deleteNote}
            onPin={togglePin}
            onUpdate={updateNote}
          />
        ))}
      </div>
      <button className="add-note-btn" onClick={addTemporaryNote} title="Add new note">
        ＋
      </button>
    </div>
  );
};

export default NoteCanvas;
